INSERT INTO [dbo].[Questions] ([Id], [title], [text], [pos], [answer], [other], [parent]) VALUES (26, N'Что такое расширенная заявка?', N'Расширенная заявка – это заявка с бОльшим количеством полей, заполнение которых позволит Вам получить предварительное одобрение по клиенту.
INSERT INTO [dbo].[Questions] ([Id], [title], [text], [pos], [answer], [other], [parent]) VALUES (27, N'Что дает предварительное одобрение?', N'Предварительное одобрение – это полноценная проверка всех введенных данных по базам (БКИ и пр.). Сама проверка занимает меньше 1 минуты. Если Вы не хотите получать предварительное одобрение – можете передать заявку в работу в любой удобный момент. Чем больше полей заполнено, тем меньше времени понадобится Вашему куратору на передачу заявки на полноценное одобрение.', 1, 1, 0, 4)
INSERT INTO [dbo].[Questions] ([Id], [title], [text], [pos], [answer], [other], [parent]) VALUES (28, N'Где я могу загрузить весь пакет документов?', N'После заполнения короткой заявки или после перехода в расширенную, у Вас появится раздел «Документы», в который Вы сможете сохранить все необходимые файлы. Ваш куратор получить уведомление о загруженных документах и сможет найти их в специальной папке, связанной с заявкой.', 2, 1, 0, 4)
