INSERT INTO [dbo].[Questions] ([Id], [title], [text], [pos], [answer], [other], [parent]) VALUES (34, N'Как я узнаю на каком этапе моя заявка', N'В разделе «Мои клиенты» Вы можете найти столбец «Этап заявки». В ней всегда актуальный статус по той или иной заявке.', 1, 1, 0, 5)
